            arr[x] = matrix[i][j];
            if(up){
                if(j == m-1){ i++; up = false; }
                else if(i == 0){ j++; up = false; }
                else{ i--; j++; }
            }
            else{
                if(i == n-1){ j++; up = true; }
                else if(j == 0){ i++; up =true; }
                else{ i++; j--; }
            }
        }
        return arr;
    }
}

