```java
public int[] findDiagonalOrder(int[][] matrix) {
  int m = matrix.length;
  if (m == 0)
    return new int[0];
  int n = matrix[0].length;
  int[] answer = new int[m*n];

  int x = 0;
  int y = 0;
  int index = 0;

  // (m + n - 1) repeats the diagonal
  for (int i = 0; i < m + n - 1; i++) {
    // i is even number: Right upward
    if (i % 2 == 0) {
      answer[index++] = matrix[x][y];
      while (x > 0 && y < matrix[0].length-1) {
        x--;
        y++;
        answer[index++] = matrix[x][y];
      }
	  // After going all the way to the right upward, decide the starting point of next left downward loop
      if (y != matrix[0].length-1)  y++;
      else x++;
    }
    // i is odd number: Left downward
    else {
      answer[index++] = matrix[x][y];
      while (y > 0 && x < matrix.length-1) {
        x++;
        y--;
        answer[index++] = matrix[x][y];
      }
      // After going all the way to the left downward, decide the starting point of next right upward loop
      if (x != matrix.length-1)  x++;
      else y++;
    }
  }

  return answer;
}
