class Solution {
private:
// Answer variable is declared here for ease of access
    vector<int> ans;
    // Function to traverse in the bottom left direction and add the values to answer
    void bottom_left(vector<vector<int>>& mat, int& m, int& n){

        while(m >= 0 && m < mat.size() && n >= 0 && n < mat[0].size()){
            ans.push_back(mat[m][n]);
            m++;
            n--;
        }
    }
    // Function to traverse in the top right direction and add the values to answer
    void top_right(vector<vector<int>>& mat, int& m, int& n){

        while(m >= 0 && m < mat.size() && n >= 0 && n < mat[0].size()){
            ans.push_back(mat[m][n]);
            m--;
            n++;
        }
    }
public:
    vector<int> findDiagonalOrder(vector<vector<int>>& mat) {
        // initial position is 0, 0
        int m = 0;
        int n = 0;

        // variable to determine if we go in the top-right or bottom-left directions
        int dir = 0;
        cout << ans.size();
        while(ans.size() < mat.size() * mat[0].size()){
            if(dir == 0){
                // for(auto i : ans)cout << i << " ";
                // cout << endl;
                top_right(mat, m, n);

                if(n > mat[0].size() - 1){
                    m +=2;
                    n--;
                }
                else if(m < 0){
                    m++;
                }

                dir = 1;
            }
            else{
                // for(auto i : ans)cout << i << " ";
                // cout << endl;
                bottom_left(mat, m, n);

                if(m >= mat.size()){
                    n += 2;
                    m--;
                }
                else if(n < 0){
                    n++;
                }
                dir = 0;
            }
        }
        // for(auto i : ans)cout << i << " ";
        //         cout << endl;
        // cout << "complete" << endl;
        return ans;
    }
};
```

