class Solution {
    public int[] findDiagonalOrder(int[][] mat) {
        int m = mat.length;
        int n = mat[0].length;

        int[] ans = new int[m * n];
        int index = 0;

        int i = 0;
        int j = 0;
        ans[index++] = mat[i][j];

        boolean isUp = true;
        while(index != ans.length) {
            if(isUp) {
                j += 1;

                while(j != 0 && i != m - 1) {
                    if(j < n) {
                        ans[index++] = mat[i][j];
                    }
                    i++;
                    j--;
                }

                ans[index++] = mat[i][j];
                isUp = false;
            } else {
                i += 1;

                while(i != 0 && j != n - 1) {
                    if(i < m) {
                        ans[index++] = mat[i][j];
                    }
                    i--;
                    j++;
                }

                ans[index++] = mat[i][j];
                isUp = true;
            }
        }

        return ans;
    }
}
