class Solution {
public:
    vector<int> findDiagonalOrder(vector<vector<int>>& mat) {
        int n = mat.size();
        int m = mat[0].size();
        vector<int>v;

        int i = 0,j = 0;
        bool flag = true;
        dfs(mat,i,j,v,flag);

        return v;
    }

    void dfs(vector<vector<int>>& mat,int i,int j ,vector<int>&v ,bool &flag)
    {
        if(i>= 0 and i<mat.size() and j>= 0 and j<mat[0].size())
        {
            v.push_back(mat[i][j]);
            if(flag)
                dfs(mat,i-1,j+1,v,flag);
            else
                dfs(mat,i+1,j-1,v,flag);
        }
        else if(i < 0 and j>=0 and j<mat[0].size())
        {
            flag = false;
            dfs(mat,i+1,j,v,flag);
        }
        else if(j<0 and i>= 0 and i<mat.size())
        {
            flag = true;
            dfs(mat,i,j+1,v,flag);
        }
        else if(i<0 and j>=mat[0].size())
        {
            flag = false;
            dfs(mat,i+2 ,j-1,v,flag);
        }
        else if(i>=mat.size() and j<0)
        {
            flag = true;
            dfs(mat,i-1,j+2,v,flag);
        }
        else if(j>=mat[0].size() and i>= 0 and i<mat.size())
        {
            flag = false;
            dfs(mat,i+2,j-1,v,flag);
        }
        else if(i>=mat.size() and j>= 0 and j<mat[0].size())
        {
            flag = true;
            dfs(mat,i-1,j+2,v,flag);
        }
        return;
    }
};

