func findDiagonalOrder(mat [][]int) []int {

    // Read diagonals from top to down
    diagonals := make([][]int, len(mat)+len(mat[0]))
    for i := 0; i < len(mat); i++ {
        for j := 0; j < len(mat[0]); j++ {
            diagonals[i+j] = append(diagonals[i+j], mat[i][j])
        }
    }

    // Concantenate diagonals reverting even ones
    res := make([]int, 0, len(mat)*len(mat[0]))
    for diagNum, d := range diagonals {
        if diagNum % 2 == 0 {
            for i, j := 0, len(d)-1; i < j; i, j = i+1, j-1 {
                d[i], d[j] = d[j], d[i]
            }
        }
        res = append(res, d...)
    }

    return res
}
