class Solution {
    public int[] findDiagonalOrder(int[][] arr) {
        int row = arr.length; 
        int col = arr[0].length;
        int[] output = new int[row * col];
        int r = 0;
        int c = 0;
        int k = 0;
        while (r < row && c < col) {
            // go up
            while (r >= 0 && c < col) {
                output[k++] = arr[r][c];
                r--;
                c++;
            }
            if (c < col) {
                r = 0;
            } else {
                c = c - 1;
                r = r + 2;
            }
            // go down
            while (r < row && c >= 0) {
                output[k++] = arr[r][c];
                r++;
                c--;
            }
            if (r < row) {
                c = 0;
            } else {
                r = r - 1;
                c = c + 2;
            }
        }
        return output;
    }
}
