class Solution {

    //  Time Complexity = O(N*M)
    //  Space Complexity = O(N*M) This solution is space extensive since we are using different auxilliary
    //          data structures to store elements like Map and List apart from resultant array.

    public int[] findDiagonalOrder(int[][] matrix) {

        //  Edge case
        if( matrix == null || matrix.length ==0){
            return new int[0];
        }
        //  Length of rows and columns
        int rows = matrix.length;
        int cols = matrix[0].length;

        //  Resultant Array to put the Diagonal traversal of the given matrix  
        int[] result = new int[rows * cols];

        // The sum of indices of diagonal elements are same 
        Map<Integer,List<Integer>> map = new HashMap<>();

        for(int i = 0 ; i < rows ; i++){
            for( int j=0; j < cols; j++){
                //  If the key(i+j) doesn't exist, then put it in the map
                if(! map.containsKey( i + j)){
                     map.put( i+ j, new ArrayList<>());
                }
                //  Add the element on the corresponding key(i+j)
                map.get(i+j).add(matrix[i][j]);
            }
        }

        int lastElement = rows + cols-2;
        int index =0;

        for( int diag = 0; diag <= lastElement ; diag++ ){

            List<Integer> list = map.get(diag);
            //  if diagonal is even, we get elements in reverse direction of diagonal
            if(diag %2 ==0){
                Collections.reverse(list);
            }
            for(int dt : list){
                result[index++]= dt;
            }
        }
        return result;   
    }    
}
