class Solution:
    def findDiagonalOrder(self, mat: List[List[int]]) -> List[int]:
        m=len(mat)
        n=len(mat[0])
        arr=[]
        i,j=0,0
        if m==1:
            return mat[0]
        flip=1
        while i>=0 and j>=0 and i<m and j<n:
            tmp=[]
            r,c=i,j
            while r>=0 and c<n:
                tmp.append(mat[r][c])
                r,c=r-1,c+1
            if flip>0:
                arr.extend(tmp)
            else:
                arr.extend(tmp[::-1])
            if i<m-1:
                i+=1
            elif i==m-1:
                j+=1
            flip*=-1    

        return arr    

