class Solution {
    public int[] findDiagonalOrder(int[][] mat) {
        int m = mat.length, n = mat[0].length;
        int[] res = new int[m*n];
        int row = 0, col = 0;
        for (int i=0; i<m*n; i++) {
            res[i] = mat[row][col];
            // up arrow: sum of row and col indices is even
            if ((row+col) % 2 == 0) {
                // hit right side
                if (col + 1 == n) {
                    row++;
                } else if ( row == 0) {  // hit top side
                    col++;
                } else {
                    row--;
                    col++;
                }
            } 
            // down arrow: sum of row and col indices is odd
            else if ((row+col) % 2 != 0) {
                if (row + 1 == m) { // hit bottom
                    col++;
                } else if (col == 0) { // hit left
                    row++;
                } else {
                    row++;
                    col--;
                }
            }
        }
        return res;
    }
}
