class Solution {
public:
    vector<int> findDiagonalOrder(vector<vector<int>>& matrix) {

        int m = matrix.size();
        int n = matrix[0].size();

        int i = 0, j = 0;

        bool dir = true;
        int end = m*n;
        vector<int> ans(end);
        int start = 0;

        while (start<end) {
            ans[start] = matrix[i][j];
            int new_row = i + (dir ? -1 : 1);//go up/down
            int new_column = j + (dir == 1 ? 1 : -1);//go right/left
            if (new_row < 0 || new_row > m-1 || new_column < 0 || new_column > n-1) {
                if (dir) {//moving toward top right
                    //case: new_row < 0 , new_column > n-1
                    i += (j == n - 1 ? 1 : 0);//reach boundary, go down
                    j += (j < n - 1 ? 1 : 0);//keep moving to right

                }else{//moving toward bottom left
                    //case: new_row < 0, new_column
                    j += (i == m - 1 ? 1 : 0);//reach bottom, move right
                    i += (i < m - 1 ? 1 : 0);//keep moving down
                }
               dir=!dir;    
            } else {
                i = new_row;
                j = new_column;
            }
            start++;
        }
        return ans;  
    }
};
