class Solution {
public:
    vector<int> findDiagonalOrder(vector<vector<int>>& mat) {
        int row=mat.size(),col=mat[0].size(),gap=0;
        vector<int>ans;
        while(gap<=row+col-2){
            int topRow=0,topCol=gap,bottomRow=gap,bottomCol=0;
            if(gap>=col) topCol=col-1,topRow=gap-col+1;
            if(gap>=row) bottomRow=row-1,bottomCol=gap-row+1;
            if(gap%2==0) for(int i=bottomRow;i>=topRow;i--) ans.push_back(mat[i][bottomCol]),bottomCol++;//bottom to top
            else for(int i=topRow;i<=bottomRow;i++) ans.push_back(mat[i][topCol]),topCol--;//top to bottom       
            ++gap;            
        }
        return ans;               
    }
};

```
