class Solution {
public:
    vector<int> findDiagonalOrder(vector<vector<int>>& mat) {
        int r = mat.size(), c = mat[0].size();
        int i = 0, j = 0;
        vector<int> vec;
        int dir = 1;
        while (vec.size() < r * c) {
            vec.push_back(mat[i][j]);
            if (i == 0 && j == c - 1 && dir == 1) {
                ++i; dir = -1;
            } else if (i == 0 && dir == 1) {
                ++j; dir = -1;
            } else if (j == 0 && i == r - 1 && dir == -1) {
                ++j; dir = 1;
            } else if (j == 0 && dir == -1) {
                ++i; dir = 1;
            } else if (i == r - 1 && dir == -1) {
                ++j; dir = 1;
            } else if (j == c - 1 && dir == 1) {
                ++i; dir = -1;
            } else if (dir == 1) {
                --i; ++j;
            } else {
                ++i; --j;
            }
        }

        return vec;
    }
};
