class Solution {
    public int maxCount(int m, int n, int[][] ops) {
        int xmin = m , ymin = n;
        int i = 0, j = ops.length - 1;
        while (i <= j) {
            xmin = Math.min(xmin,Math.min(ops[i][0], ops[j][0]));
            ymin = Math.min(ymin,Math.min(ops[i++][1], ops[j--][1]));
        }
        return xmin*ymin;
    }
}
