class Solution {

    // Get the count of max integers
    // We know the cell at M[0][0] will ALWAYS be incremented for every operation,
    // so we only need to keep track of how many cells are incremented on EVERY operation.
    public int maxCount(int m, int n, int[][] ops) {

        // Iterate for every operator in the array
        for (int[] op : ops) {

            // If the first dimension in the operation is less than m, then our
            // solution set shrinks. Set m equal to that dimension.
            if (op[0] < m) {
                m = op[0];
            }

            // Same goes for the second dimension. If it is less than n, our solution
            // set shrinks. Set n equal to that dimension.
            if (op[1] < n) {
                n = op[1];
            }
        }

        // Return the number of cells that were incremented every time, m*n
        return m * n;
    }
}

