        if (ops == null || ops.length == 0) {
            return m * n;
        }

        int minRow = Integer.MAX_VALUE;
        int minCol = Integer.MAX_VALUE;
        for (int[] op : ops) {
            int row = op[0];
            if (row < minRow) {
                minRow = row;
            }
            int col = op[1];
            if (col < minCol) {
                minCol = col;
            }            
        }

        return minRow * minCol;
    }
}
