    public int maxCount(int m, int n, int[][] ops) {
        int col[]=new int [m];
        int row[]=new int [n];

        for(int op[]:ops){
            for(int i=0;i<op[0];i++) col[i]++;
            for(int j=0;j<op[1];j++) row[j]++;
        }

        int i=0,j=0;
        while(i<m && col[0]==col[i]) i++;
        while(j<n && row[0]==row[j]) j++;

        return i*j;
    }
}

