// Needed for allowing pair<> to be used as a key in unordered_map
struct pair_hash {
    template <class T1, class T2>
    std::size_t operator () (const std::pair<T1,T2> &p) const {
        auto h1 = std::hash<T1>{}(p.first);
        auto h2 = std::hash<T2>{}(p.second);

        // Mainly for demonstration purposes, i.e. works but is overly simple
        // In the real world, use sth. like boost.hash_combine
        return h1 ^ h2;
    }
};

class Solution {
public:
    int maxCount(int m, int n, vector<vector<int>>& ops) {
        if (ops.size() == 0)    return m*n;
        unordered_map<pair<int,int>,int, pair_hash> posToCountMap;
        for (int i=0; i<ops.size(); i++)
            for (int j=0; j<ops[i][0]; j++)
                for (int k=0; k<ops[i][1]; k++)
                    posToCountMap[pair<int,int>(j,k)]++;

        int maxNum = posToCountMap[pair<int,int>(0,0)];
        int maxNumElems = 0;
        for (const auto& elem : posToCountMap)      maxNumElems += (elem.second == maxNum);

        return maxNumElems;
    }
};
