```swift
class Solution {
    func convert(_ s: String, _ numRows: Int) -> String {
        guard s.count > 1 && numRows > 1 else { return s }

        var lines = [[Character]](repeating: [], count: numRows)
        var idx = 0
        var flag = false

        for ch in s {
            lines[idx].append(ch)
            idx += flag ? -1 : 1
            guard idx == -1 || idx == numRows else { continue }
            idx += flag ? 2 : -2
            flag.toggle()
        }
        return lines.map { String($0) }.joined()
    }
}
