class Solution {
public:
    string convert(string s, int numRows) {
        int S = s.length(), index = 0, alen = S > numRows ? S : numRows, direction=1; 
        string vs[alen+1], ans;

        for (int i = 0; i < S; i++) {
            vs[index] += s[i];
            if (index == numRows-1 || index == 0) {
                direction = 1 - direction;
            }
            if (!direction) {
                index += 1;
            } else { 
                index -= 1;
            }

        }
        for (int i = 0; i <= alen; i++) {
            ans += vs[i];
        }

        return ans;
    }
};

