class Solution {
public:

    string convert(string s, int n) {
     string ans="";
     int i=1;
     int j=0,f=0;
     int jmp=2*n-2;//long jump condition
     bool centre=true;

if(n==1 || s.size()<=n)return s;
//loop for first row
while(f<s.size()){
    ans.push_back(s[f]);
    f+=jmp;
     }
     //loop for between rows 
     while(i<n-1){
         j=i;
         centre = true;
         ans.push_back(s[i]);
         //loop to push a row
     while(j<s.size())
     {
     if(centre){
     if(j+jmp-2*i<s.size())//short jump condition for centre element 
     ans.push_back(s[j+jmp-2*i]);
    centre=false;
    }
    else
    {
    j+=jmp; //long jump similar to first and last row 
    if(j<s.size())
    ans.push_back(s[j]);
    centre=true;
    }
  }
 i++;
 }

f=n-1;
//loop for last row
while(f<s.size())
{
    ans.push_back(s[f]);
    f+=jmp;
}
return ans;
} 
};
```

