impl Solution {
    pub fn convert(s: String, num_rows: i32) -> String {
        if num_rows == 1 { return s; }
        let mut results: Vec<String> = vec![String::new();num_rows as usize];
        let mut letters = s.chars();

        let mut index:usize = 0;
        let mut direction:bool = true;

        while let Some(letter) = letters.next(){
            results[index].push(letter);
            if direction { index += 1; } else { index -= 1;}

            if index != num_rows as usize && index != usize::MAX { continue; }

            direction = !direction;
            if direction { index += 2; } else { index -= 2;}
        }

        for index in 1..num_rows as usize {
            let temp = &results[index].clone();
            results[0].push_str(temp);
        }
        results[0].clone()
    }
}
```

