class Solution {
public:
    string convert(string s, int row) {
        if(row==1)return s;

        string ans;
        int p=0,n=s.length();

        // row=0
        while(p<n){
            ans.push_back(s[p]);
            p += (row-1)*2;
        }

        //middle rows
        for(int i=1 ; i<row-1 ; i++){
            p = i;
            bool down=true;
            while(p<n){
                ans.push_back(s[p]);
                if(down){
                    p += (row-1-i)*2;
                }
                else{
                    p += i*2;
                }
                down = !down;
            }
        }

        // last row
        p=row-1;
        while(p<n){
            ans.push_back(s[p]);
            p += (row-1)*2;
        }
        return ans;
    }
};
