class Solution(object):
def convert(self, s, numRows):
    if numRows <2: return s
    n, m, i = len(s),numRows+numRows-2, 0
    rows = ['']*numRows
    while i < n:
        r = min(i%m, m-i%m)
        rows[r] += s[i]
        i += 1
    return ''.join(rows)

