class Solution {
public:
    string convert(string s, int numRows) {
        // we know numrows 
        // for column we will try some math 
        int size=s.length();

        if(size<=numRows){
            return s;
        }

        else{
            int col=0;
            int l=size;

            while(l!=0){
                if(l>=(numRows)+(numRows-2)){
                    col=col+(numRows-1);
                    l=l-(numRows)+(numRows-2);
                }

                else if(l>numRows && l<(numRows)+(numRows-2) ){
                    col=col+(1+l-numRows);
                    l=0;
                }

                else if(l<=numRows){
                    col=col+1;
                    l=0;
                }
            }

            char mat[numRows][col];
            int index=0;
            int i;
            for(int j=0;j<col;j++){
                for( i=0;i<numRows;i++){
                    mat[i][j]=s[index];
                    index++;
                }
                while(i>0){
                    j++;
                    i--;
                    mat[i][j]=s[index];
                    index++;
                }
            }

            string res;
            for(int i=0;i<numRows;i++){
                for(int j=0;j<col;j++){
                    if(mat[i][j]!=NULL){
                        res=res+mat[i][j];
                    }
                }
            }

            return res;
        }

        return 0;
    }
};

