class Solution {
    public String convert(String s, int numRows) {
        String arr[][]=new String[numRows][s.length()];

        int row=0;
        int col=0;
        int cur=0;

        while(cur<s.length()){
            while(row<numRows && cur<s.length()){
                arr[row++][col]=s[cur++];
            }
            row=Math.max(0,row-2);
            while(row>0 && cur<s.length()){
                arr[row--][++col]=s[cur++];
            }
            col++;
        }

        String ans=" ";

        for(int i=0;i<numRows;i++){
            for(int j=0;j<s.length();j++){
                ans+=arr[i][j];
            }
        }
        return ans;
    }
}

