    public int helper(int[][] grid,int r,int c,int m,int n){
        if(r == m || c == n || grid[r][c] == 1)return 0;
        if(r == m-1 && c == n-1){
            if(grid[r][c] == 1)return 0;
            return 1;
        }
        return helper(grid,r+1,c,m,n)+helper(grid,r,c+1,m,n);
