                else if (j == 0) //if this is first col, and moving down direction then we can do whatever we did on previous step
                    dp[i][j] = dp[i - 1][j];
                else
                    dp[i][j] = dp[i - 1][j] + dp[i][j - 1]; //we can come here either down or right
