        int n = obstacleGrid[0].length;

        int dp[][] = new int[m][n];
        for (int i = 0; i < m; i++)
            Arrays.fill(dp[i], -1);

        final int sx = 0, sy = 0;
        final int dx = m - 1, dy = n - 1;

