        }
        return 0;
    }

    private boolean isSafe(int sx, int sy, int m, int n, int[][] maze) {
        if (sx >= m || sy >= n || sx < 0 || sy < 0 || maze[sx][sy] == 1) //Line change from UniqPathsI
            return false;
        return true;
    }
}

