        if(m<0 or n<0 or grid[m][n]==1) return 0;
		if(m==0 && n==0) return 1;
        int left=f(m,n-1,grid);
        int up=f(m-1,n,grid);
        return left+up;
    }
    int uniquePathsWithObstacles(vector<vector<int>>& grid) {
        int m=grid.size(), n=grid[0].size();
        return f(m-1,n-1,grid);
