class Solution {
public:
    int helper(vector<vector<int>>& grid, int i, int j, int t[101][101]){
        if(i==grid.size() || j==grid[0].size()) return 0; // base case
        if(grid[i][j]==1)return 0; // obstacle
        if(i==grid.size()-1 && j==grid[0].size()-1)return 1; // destiny
        if(t[i][j]!=-1)return t[i][j]; // already calculated
        return t[i][j] = helper(grid, i+1, j, t) + helper(grid, i, j+1, t);
    }

    int uniquePathsWithObstacles(vector<vector<int>>& grid) {
        int t[101][101];
        memset(t, -1, sizeof t);
        return helper(grid, 0, 0, t);

    }
};
