class Solution { 
public:
    int uniquePathsWithObstacles(vector<vector<int>>& obstacleGrid) {
        int m = obstacleGrid.size(), n = obstacleGrid[0].size();
        vector <int> prev(n, 0);
        for (int i = 0; i < m; i++) {
            vector <int> cur(n, 0);
            for (int j = 0; j < n; j++) {
                if (obstacleGrid[i][j] == 1) {
                    cur[j] = 0;
                    continue;
                }
                if (i == 0 && j == 0) {
                    cur[j] = 1;
                    continue;
                }
                int left = 0, up = 0;
                if (j > 0)
                    left = cur[j-1];
                if (i > 0)
                    up = prev[j];    
                cur[j] = left + up;
            }
            prev = cur;
        }
        return prev[n-1];
    }
};
```

