	grid[0][0] ^= 1
	for i,j in zip(range(1, n), range(1, m)):
		grid[0][i] = not grid[0][i] & grid[0][i - 1]
		grid[j][0] = not grid[j][0] & grid[j - 1][0]        
	for i in range(1, m):
		for j in range(1, n):
			grid[i][j] = grid[i - 1][j] + grid[i][j - 1] if not grid[i][j] else 0
	return grid[m - 1][n - 1]
