class Solution {

    private int util(int[][] b, int x, int y, int[][] dp)
    {
        //base case check - if x or y is invalid or if there is obstacle
        if( x < 0 || y < 0 || b[x][y] == 1)
            return 0;

        // if we reached the cell (0,0) - return 1 as there is a path
        if(x == 0  && y == 0 )
            return 1;

	// check your cache to avoid repeated check
        if(dp[x][y] != Integer.MAX_VALUE)
            return dp[x][y];

	// else go to the top or left cell and store back the result        
        dp[x][y] = util(b, x-1, y, dp) + util(b, x, y-1, dp);

        return dp[x][y];

    }

    public int uniquePathsWithObstacles(int[][] obstacleGrid) {
       //base case
        if(obstacleGrid.length == 0)
            return 0;

        if(obstacleGrid.length == 1 && obstacleGrid[0].length ==1)
            return obstacleGrid[0][0] == 0 ? 1 : 0;

        int[][] dp  = new int[obstacleGrid.length][obstacleGrid[0].length];

	// initialize DP array
        for(int i=0; i < dp.length; i++)
            Arrays.fill(dp[i], Integer.MAX_VALUE );

        //start from the last for this question
        int m = obstacleGrid.length -1 ;
        int n = obstacleGrid[0].length -1;

       // Start from the bottom right most cell and go till (0,0) cell
        util(obstacleGrid, m, n, dp);

        return dp[m][n] != Integer.MAX_VALUE ? dp[m][n] : 0; 
    }
}
