class Solution:
    def uniquePathsWithObstacles(self, obstacleGrid: List[List[int]]) -> int:
        self.height, self.width = len(obstacleGrid), len(obstacleGrid[0])
        self.memo = defaultdict(int)

        def walk(i, j):
            # If were on an obstacle
            if obstacleGrid[i][j] == 1: return 0

            # If we arrived to the end we know there is a path
            if i == self.height - 1 and j == self.width - 1:
                return 1

            # Memo
            if (i, j) in self.memo:
                return self.memo[(i, j)]

            # Travel down
            if i < self.height - 1:
                self.memo[(i, j)] += walk(i + 1, j)

            # Travel right
            if j < self.width - 1:
                self.memo[(i, j)] += walk(i, j + 1)

            return self.memo[(i, j)]


        return walk(0, 0)
