class Solution {
public:
// this function will return all possible paths corresponding to row and col....
    int paths(int row,int col,vector<vector<int>>&obstacleGrid,vector<vector<int>>&dp,int n,int m){
        // base cases:
        // 1. if robot goes outside grid or on the cell having obstacle
        if(row<0||col<0||row>=n||col>=m||obstacleGrid[row][col]==1){
            return 0;
        }
        // 2. If robot is able to reach the bottom right cell...
        if(row==n-1&&col==m-1){
            return 1; // as we have found a particular combination..
        }
        // repetative cases:
        if(dp[row][col]!=-1){
            return dp[row][col];
        }
        vector<pair<int,int>>dir={{0,1},{1,0}};
        int ans=0; // it is all possible paths...
        for(auto ele:dir){
            int nr=row+ele.first;
            int nc=col+ele.second;
            ans+=paths(nr,nc,obstacleGrid,dp,n,m);
        }
        return dp[row][col]=ans;
    }
    int uniquePathsWithObstacles(vector<vector<int>>& obstacleGrid) {
        int n=obstacleGrid.size(),m=obstacleGrid[0].size();
        vector<vector<int>>dp(n+1,vector<int>(m+1,-1)); // this vector is used for memoization...
        return paths(0,0,obstacleGrid,dp,n,m);
    }
};
```

