        for(int i=oG.size()-2;i>=0;i--){
            if(oG[i][oG[0].size()-1]!=1){
                dp[i][oG[0].size()-1]=1;
            }
            else break;
        }
        for(int j=oG[0].size()-2;j>=0;j--){
            if(oG[oG.size()-1][j]!=1){
                dp[oG.size()-1][j]=1;
            }
            else break;
        }

        for(int i=oG.size()-2;i>=0;i--){
            for(int j=oG[0].size()-2;j>=0;j--){
                long long ans=0;
                long long right=0,down=0;
                right = dp[i][j+1];
                down = dp[i+1][j];
                ans = right + down;
                if(oG[i][j]!=1)
                    dp[i][j]=ans;
            }
        }
        return dp[0][0];
    }

