        int dp[101][101];
        memset(dp, -1, sizeof(dp));

        int c=0;

        for(int i=0;i<n;i++){
            if(arr[0][i]==1){
                dp[0][i]=0;
                c=1;
            }
            else if(c!=1)
                dp[0][i]=1;
            else dp[0][i]=0;

