class Solution {
public:
    int dp[101][101];
    int solve(vector<vector<int>>& obstacleGrid , int x , int y)
    {
        if(x<0 || y<0)
        {
            return 0;
        }
        if(obstacleGrid[x][y] == 1)
            return 0;
        if(x == 0 && y==0)
            return 1;
        if(dp[x][y]!=-1)
            return dp[x][y];
        int ans = 0;
        ans+=solve(obstacleGrid , x - 1 , y);
        ans+=solve(obstacleGrid , x , y-1);
        return dp[x][y] = ans;
    }
    int uniquePathsWithObstacles(vector<vector<int>>& obstacleGrid) {
        int n = obstacleGrid.size();
        int m = obstacleGrid[0].size();
        if(obstacleGrid[n-1][m-1] == 1 || obstacleGrid[0][0] == 1)
            return 0;
        memset(dp,-1,sizeof(dp));
        return solve(obstacleGrid , n-1 , m-1);
    }
};
