// Use dynamic Programming, if the grid at i,j position contains obstacle, turn it to 0
// And for the rest grid[i][j] = grid[i - 1][j] + grid[i][j - 1]

// You don't even have to make new 2D vector, use the same obstacleGrid vector

class Solution {
public:
    int uniquePathsWithObstacles(vector<vector<int>>& obstacleGrid) {
        if(obstacleGrid[0][0] == 1)
            return 0;

        int i = 0, j = 1;
        int m = obstacleGrid.size();
        int n = obstacleGrid[0].size();

        while(i < m && obstacleGrid[i][0] != 1) {
            obstacleGrid[i][0] = 1;
            i++;
        }
        while(i < m) {
            obstacleGrid[i][0] = 0;
            i++;
        }

        while(j < n && obstacleGrid[0][j] != 1) {
            obstacleGrid[0][j] = 1;
            j++;
        }
        while(j < n) {
            obstacleGrid[0][j] = 0;
            j++;
        }

        for(int i = 1; i < m; ++i) {
            for(int j = 1; j < n; ++j) {
                if(obstacleGrid[i][j] == 1)
                    obstacleGrid[i][j] = 0;
                else
                    obstacleGrid[i][j] = obstacleGrid[i - 1][j] + obstacleGrid[i][j - 1];
            }
        }

        return obstacleGrid[m - 1][n - 1];
    }
};
