int uniquePathsWithObstacles(vector<vector<int>>& obstacleGrid) {
	int m = obstacleGrid.size(), n = obstacleGrid[0].size();
	vector<int> vec(n+1,0);
	vec[1]=1;
	for (int i=0;i<m;++i)
		for (int j=1;j<n+1;++j)
			vec[j] = obstacleGrid[i][j-1] == 1 ? 0 : vec[j]+vec[j-1];
	return vec[n];
}
