Recursion Solution:
```
class Solution {
private:
    int helper(int i, int j, vector<vector<int>>& A, vector<vector<int> > &Dp) {
        if(i >= A.size() || j >= A.front().size() || A[i][j] == 1) return 0;
         if(i == A.size()-1 && j == A.front().size()-1) return 1;
        if(Dp[i][j] != -1) return Dp[i][j];
        int op1 = helper(i+1, j, A, Dp);
        int op2 = helper(i, j+1, A, Dp);
        return Dp[i][j] = op1 + op2;
        }
public:
    int uniquePathsWithObstacles(vector<vector<int>>& A) {
        int r = A.size();
        int c = A.front().size();
        vector<vector<int> > Dp(r, vector<int>(c, -1));
