class Solution {
public:
    int m , n ; 
    int t[101][101];
    int solve(vector<vector<int>>&Grid , int i , int j ){
        if( i >= m || j >= n || Grid[i][j]==1){//for out of bound and obstacle case 
            return 0;
        }
        if(t[i][j] != -1) return t[i][j];
        if(i == m-1 && j == n-1){// we arrive goal 
            return 1; // found 1 ways to reach at goal 
        }
        int right=solve(Grid , i , j+1);//for ways to go right our column is increase by 1(J+1)
        int down=solve(Grid, i+1 , j);//for ways to go down our row(i) is inc. by 1(i+1)
        return t[i][j] = right+down; // return both ways to acheive goal
    }

    int uniquePathsWithObstacles(vector<vector<int>>& obstacleGrid) {
        m = obstacleGrid.size();
        n = obstacleGrid[0].size();
        memset(t ,-1 , sizeof(t));

        return solve( obstacleGrid , 0 , 0 );
    }
};
