class Solution {
public:

int dp[101][101];
int n,m;

int dx[2] = {1,0};   // Direction arrays for moving down or to the right
int dy[2] = {0,1};



bool isok(int nx,int ny). 
{
  return nx>= 0 && ny>=0 && nx<=n-1 && ny<=m-1;
}


int solve(int x,int y,vector<vector<int>>& grid)
{
  if(grid[x][y] == 1)
  {
    return 0;
  }

  if(dp[x][y] != -1) return dp[x][y];
  if(x == n-1 && y== m-1) return 1;

    int nx = x+dx[0];
    int ny = y+dy[0];

    int nx1 = x+dx[1];
    int ny1 = y+dy[1];

   return dp[x][y] = (isok(nx,ny) ? solve(nx,ny,grid) : 0) +
   (isok(nx1,ny1) ? solve(nx1,ny1,grid) : 0);

// Key step



}
    int uniquePathsWithObstacles(vector<vector<int>>& grid) {
        memset(dp,-1,sizeof(dp));
        n = grid.size();
        m = grid[0].size();

        return solve(0,0,grid);
    }
};
```

