```
Select id, date, people
From (
Select id, date, people,
    @rank:= If(people>=100, @rank+1, 0) AS Rank,
    @group:= If(@rank=0 or @rank=1, @group+1, @group) AS GroupNum
From stadium, (Select @rank:=0, @group:=0) var
) tmp

JOIN
(
    Select GroupNum
    From
        (
        Select id, date, people,
            @rank_:= If(people>=100, @rank_+1, 0) AS Rank,
            @group_:= If(@rank_=0 or @rank_=1, @group_+1, @group_) AS GroupNum
        From stadium, (Select @rank_:=0, @group_:=0) var
        ) tmp2
    Group By GroupNum
    Having count(*) >=3
) filter
ON tmp.GroupNum = filter.GroupNum;
