class Solution {


    public ListNode rotateRight(ListNode head, int k) {


        if(head == null){
            return head;
        }

        //First total length of linked list is calculated
        int size = 0;

        ListNode curr = head;

        while(curr!=null){
            size++;
            curr = curr.next;

        }


        if(size == 1 || k==0 || k%size == 0){
            return head;
        }

        curr = head;
        ListNode temp1 =  curr;

        //This will handle the case if k>size
        int n = k%size;

        //Traversing till the kth value 
        int i = 0;
        while(i<size-n-1){

            curr = curr.next;
            i++;
        }

        //temp2 will store the last element till k where we have to break the linked list ;
         ListNode temp2 = curr;


        //Creating a dummy node that will store the part whuch is to be rotated
        ListNode dummy = new ListNode(-1);
        ListNode temp  = dummy;//to store the initial position of dummy node;

        //traversing till the last element and attaching with dummy
        while(curr.next!=null){
            dummy.next = curr.next;
            dummy = curr.next;
            curr = curr.next;
        }

        //joining the last element with the first element
        curr.next = temp1;

        //breaking at the kth position;
        temp2.next = null;

        return temp.next;
    }
}

