//Another approach is to 
//1. Reverse the full list
//2. reverse first k size list
//3. reverse remaning list i:e (n-k)
//You will get the result
//Time complexity: O(N)
//Space complexity: O(1)

class Solution {
public:
    //Reverse a linked list
    ListNode* reverseList(ListNode* root) {
        ListNode* prev = NULL;
        ListNode* curr = root;
        while (curr != NULL) {
            ListNode* nextTemp = curr->next;
            curr->next = prev;
            prev = curr;
            curr = nextTemp;
        }
        return prev;
    }

    ListNode* rotateRight(ListNode* head, int k) {
        //If the node is NULL
        if(head==NULL)
            return 0;

        //Calculate the length of linked list

        ListNode* newhead = head;
        int length=0;
        while(newhead!=NULL){
            length++;
            newhead=newhead->next;
        }


        k = k%length;
        if(k==0)
            return head;

        //Reverse list
        head=reverseList(head);

        //Find the kth node 
        ListNode* kthnode=head;
        while(k>1 && kthnode){
            kthnode=kthnode->next;
            k--;
        }

        if(kthnode==NULL || kthnode->next==NULL)
            return head;

        //Reverse second list
        ListNode* secondhalf=reverseList(kthnode->next);
        kthnode->next=NULL;

        //Reverse first list
        ListNode* firsthalfend=reverseList(head);
        ListNode* result=firsthalfend;

        //Link firlt list to second list
        while(firsthalfend && firsthalfend->next){
            firsthalfend=firsthalfend->next;
        }
        firsthalfend->next=secondhalf;

        return result;
    }
};
