/**
 * Definition for singly-linked list.
 * public class ListNode {
 *     public int val;
 *     public ListNode next;
 *     public ListNode(int val=0, ListNode next=null) {
 *         this.val = val;
 *         this.next = next;
 *     }
 * }
 */
public class Solution {
    public ListNode RotateRight(ListNode head, int k) {
        var queue = new Queue<ListNode>();
        if(k == 0) return head;
        var first = head;
        while(head != null)
        {
            if(head.next == null && queue.Count == k){
                head.next = first;
                queue.Dequeue().next = null;
                first = queue.Count > 0 ? queue.Peek() : head;
                break;
            }
            if(queue.Count == k) queue.Dequeue(); // We do n't need to keep more than k items in the queue
            queue.Enqueue(head);
            if(head.next == null && queue.Count != k){
                k = k % queue.Count;// To avoid repeating if k is greater than items in the list
                if(k == 0) break;
                queue.Clear();
            } 
            head=head.next ?? first;
        }
        return first;
    }
}
```

