/**
 * Definition for singly-linked list.
 * struct ListNode {
 *     int val;
 *     ListNode *next;
 *     ListNode() : val(0), next(nullptr) {}
 *     ListNode(int x) : val(x), next(nullptr) {}
 *     ListNode(int x, ListNode *next) : val(x), next(next) {}
 * };
 */
class Solution {
public:
    ListNode* rotateRight(ListNode* head, int k) {
        //Boundary Case
        if(!head||!head->next||k==0){return head;}
        ListNode* cur=head;
        int len=1;
        //Iterating to last node
        while(cur->next && ++len){cur=cur->next;}
        //Creating a circular link list
        cur->next=head;
        k=k%len;
        k=len-k;
        //Iterating to rotated list 
        while(k--){cur=cur->next;}
        //Cutting circular link list and adding headl
        head=cur->next;
        cur->next=NULL;
        return head;
    }
};
```

